// Navigation interactions
        function toggleMobileMenu() {
            const menu = document.getElementById('mobile-menu');
            menu.classList.toggle('open');
        }

        function toggleSidebar() {
            const sidebar = document.getElementById('sidebar');
            const overlay = document.getElementById('sidebar-overlay');
            sidebar.classList.toggle('open');
            overlay.classList.toggle('open');
        }

        // Tab navigation
        document.querySelectorAll('.nav-tabs .nav-link').forEach(link => {
            link.addEventListener('click', function(e) {
                e.preventDefault();
                // Remove active class from all tabs
                document.querySelectorAll('.nav-tabs .nav-link').forEach(l => l.classList.remove('active'));
                // Add active class to clicked tab
                this.classList.add('active');
            });
        });

        // Vertical navigation
        document.querySelectorAll('.navbar-nav.vertical .nav-link').forEach(link => {
            link.addEventListener('click', function(e) {
                e.preventDefault();
                // Remove active class from all nav items
                this.parentElement.parentElement.querySelectorAll('.nav-link').forEach(l => l.classList.remove('active'));
                // Add active class to clicked item
                this.classList.add('active');
            });
        });

        // Simple form interactions
        document.querySelectorAll('.file-input input[type="file"]').forEach(input => {
            input.addEventListener('change', function() {
                const label = this.parentElement.querySelector('.file-input-label');
                if (this.files.length > 0) {
                    label.innerHTML = `<span>ðŸ“„</span> ${this.files[0].name}`;
                }
            });
        });

        // Drag and drop for file inputs
        document.querySelectorAll('.file-input-label').forEach(label => {
            label.addEventListener('dragover', function(e) {
                e.preventDefault();
                this.classList.add('dragover');
            });

            label.addEventListener('dragleave', function() {
                this.classList.remove('dragover');
            });

            label.addEventListener('drop', function(e) {
                e.preventDefault();
                this.classList.remove('dragover');
                const files = e.dataTransfer.files;
                if (files.length > 0) {
                    this.innerHTML = `<span>ðŸ“„</span> ${files[0].name}`;
                }
            });
        });

        // Close mobile menu when clicking outside
        document.addEventListener('click', function(e) {
            const menu = document.getElementById('mobile-menu');
            const toggle = document.querySelector('.menu-toggle');

            if (!toggle.contains(e.target) && !menu.contains(e.target)) {
                menu.classList.remove('open');
            }
        });
